import React from 'react';

import StripeCheckOut from 'react-stripe-checkout';

const stripeCheckoutButton = ({ price }) => {
	const priceForStripe = price * 100;
	const publishableKey = 'pk_test_s3Ee1fFr9JZDseA4PGhLbaGO00xTcucg4z';
	const onToken = token => {
		console.log(token)
		alert('Payment Successful')
	}
	return (
    <StripeCheckOut label='Pay Now' name='CRWN Clothing Ltd.' billingAddress shippingAdress image='https://svgshare.com/i/Cuz.svg' description={`Your Total is $${price}`} amount={priceForStripe} panelLabel='Pay Now' token='onToken' stripeKey={publishableKey} />);
};

export default StripeCheckoutButton;
